import React, { Component } from 'react';
import { Button, Card } from 'semantic-ui-react'
import './WorkoutCard.css'


class WorkoutCard extends Component {
    state = {
        cardClass: "movement-card"
    }

    handleClick = () => {
        this.setState({cardClass: this.state.cardClass === "movement-card"? "movement-card flipped" : "movement-card"})
    }

    handleDelete = () => {
         fetch(`https://wodshare.herokuapp.com/workouts/delete/${this.props.id}`, {
            method: "DELETE"
        }).then(workout => this.props.modifyWorkoutsArr(this.props.id))
    }

    render() {
        const { name, date, id } = this.props
        return (
            <>
                <div className="prevwod-card">
                    <h2 className="prevwod-title">"{name}"</h2>
                    <h3 className="prevwod-date"><i>{cfFormatDate}</i></h3>
                    <div className="prevwod-bar">
                    <h4 className="card-content">Score: {score}</h4>
                    <h3 className="card-content">Category: {category}</h3>
                    <h3 className="card-content">Workout: </h3>
                    {workout.split('\n').map(element => {
                        return <h4 className="card-content">{element}</h4>
                    })}
                    </div>
                </div>
            </>
        );
    }
}

export default WorkoutCard;
