import React, { Component } from 'react';
import {Link, Redirect} from "react-router-dom"
import { Grid, Menu, Segment } from 'semantic-ui-react'
import './loginForm.css'
import AthleteLogin from '../AthleteComponents.js/AthleteLogin';
import CoachLogin from '../CoachComponents.js/CoachLogin';
import CreateAccountForm from './CreateAccountForm';
import Navbar from './Navbar';


class LoginForm extends Component {
    state = { 
        activeItem: "Athlete Login"
     }

     handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state
        if (this.props.user) return <Redirect to="/"/>;
        return (
            <div className="login-container right-panel-active" id="container">
                <div className="login-form-container sign-up-container ">
                    <form action="#">
                        {/* <h1 className='login-h1'>Create Account</h1> */}
                        <input className='login-input' type="text" placeholder="Name" />
                        <input className='login-input' type="email" placeholder="Email" />
                        <input className='login-input' type="password" placeholder="Password" />
                        <input className='login-input' type="password" placeholder="Password Confirmation" />
                        <button className='login-input'>Sign Up</button>
                    </form>
                </div>
                <div className="login-form-container sign-in-container">
                    <form action="#">
                        <h1 className='login-h1'>Sign in</h1>
                        <input className='login-input' type="email" placeholder="Email" />
                        <input className='login-input' type="password" placeholder="Password" /> <br/>
                        <a className='login-a' href="#">Forgot your password?</a> <br/>
                        <button className='login-button'>Sign In</button>
                    </form>
                </div>
                <div className="overlay-container">
                    <div className="overlay">
                        <div className="overlay-panel overlay-left">
                            <h1 className='login-h1'>Welcome Back!</h1>
                            <p className='login-p'>To keep connected with us please login with your personal info</p>
                            <button className="login-button ghost" id="signIn">Sign In</button>
                        </div>
                        <div className="overlay-panel overlay-right">
                            <h1 className='login-h1'>Hello, Friend!</h1>
                            <p className='login-hp'>Enter your personal details and start journey with us</p>
                            <button className="login-button ghost" id="signUp">Sign Up</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default LoginForm;


