import React, { Component } from 'react';
import {Link, Redirect} from "react-router-dom"
import { Grid, Menu, Segment } from 'semantic-ui-react'
import './loginForm.css'
import AthleteLogin from '../AthleteComponents.js/AthleteLogin';
import CoachLogin from '../CoachComponents.js/CoachLogin';
import CreateAccountForm from './CreateAccountForm';
import Navbar from './Navbar';


class LoginForm extends Component {
    state = { 
        athleteLogin: false, 
        coachLogin: false
     }

     handleAthleteClick = () => {
         this.setState({
             athleteLogin: true,
             coachLogin: false
         })
     }
     handleCoachClick = () => {
         this.setState({
             athleteLogin: false,
             coachLogin: true
         })
     }

    render() {
        const { activeItem } = this.state
        if (this.props.user) return <Redirect to="/"/>;
        return (
            <>
                {!this.state.athleteLogin || !this.state.coachLogin?
                <>
                    <button onClick={this.handleAthleteClick} className='choice-button'>Athlete</button> <br/>
                    <h1>------OR------</h1>
                    <button onClick={this.handleCoachClick} className='choice-button'>Coach</button>
                </>
                :
                undefined
                }
            </>
        );
    }
}

export default LoginForm;


