import API from './API.js';
import './App.css';
import { BrowserRouter as Router, Route, Link, Switch, Redirect } from 'react-router-dom'
import LoginForm from './MainComponents/LoginForm';


class App extends React.Component {

  state = {
    user: undefined,
    loginError: false
  }

  componentDidMount(){
    if (API.hasToken()) {
      API.validate().then(this.setState({user}))
        .then(() => this.setState({loginError: false}))
    } else {
      this.setState({loginError: true})
    }
  }

   logout = () => {
    setUser(false);
    API.clearToken();
  }
  render(){
    return (
      <>
      <Switch>
        <Route path = '/login'>
            <LoginForm setError = {setError} handleLogin = {setUser} user = {user}/>
        </Route>
      </Switch>
      </>
    );
  }
}

export default App;
