import React, { Component } from 'react';
import {Link, Redirect} from "react-router-dom"
import { Button, Form, Grid, Menu, Segment } from 'semantic-ui-react'
import API from '../API';
import './loginForm.css'


class LoginForm extends Component {
    state = { 
        email: undefined, 
        password: undefined,
        activeItem: "Athlete Login"
     }

     handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
     }

     handleSubmit = () => {
        API.loginAthlete({
            email: this.state.email,
            password: this.state.password
        }).then(user => this.props.onSuccess(user))
     }

     handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem, email, password } = this.state
        return (
            <div className="container">
                    <Grid>
                        <Grid.Column width={4}>
                        <Menu fluid vertical tabular>
                            <Menu.Item
                            name='Athlete Login'
                            active={activeItem === 'Athlete Login'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='pics'
                            active={activeItem === 'pics'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='companies'
                            active={activeItem === 'companies'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='links'
                            active={activeItem === 'links'}
                            onClick={this.handleItemClick}
                            />
                        </Menu>
                        </Grid.Column>

                        <Grid.Column stretched width={12}>
                            <Segment>
                                <Form onSubmit={this.handleSubmit}>
                                    <h1>Athlete Login</h1>
                                    <Form.Field>
                                        <label>Email</label>
                                        <input type = "text" name = "email" value = {email} onChange = {this.handleChange}></input>
                                    </Form.Field>
                                    <Form.Field>
                                        <label>Password</label>
                                        <input type = "password" name = "password" value = {password} onChange = {this.handleChange}></input>
                                    </Form.Field>
                                    <Form.Field>
                                    </Form.Field>
                                        <Button type='submit'>Log in</Button>
                                </Form>
                            </Segment>
                            <Segment>
                                <Form onSubmit={this.handleSubmit}>
                                    <h1>Athlete Login</h1>
                                    <Form.Field>
                                        <label>Email</label>
                                        <input type = "text" name = "email" value = {email} onChange = {this.handleChange}></input>
                                    </Form.Field>
                                    <Form.Field>
                                        <label>Password</label>
                                        <input type = "password" name = "password" value = {password} onChange = {this.handleChange}></input>
                                    </Form.Field>
                                    <Form.Field>
                                    </Form.Field>
                                        <Button type='submit'>Log in</Button>
                                </Form>
                            </Segment>
                        </Grid.Column>
                    </Grid>
            </div>
        );
    }
}

export default LoginForm;


