import React, { Component } from 'react';
import {Link, Redirect} from "react-router-dom"
import { Grid, Menu, Segment } from 'semantic-ui-react'
import './loginForm.css'
import AthleteLogin from '../AthleteComponents.js/AthleteLogin';
import CoachLogin from '../CoachComponents.js/CoachLogin';
import CreateAccountForm from './CreateAccountForm';
import Navbar from './Navbar';


class LoginForm extends Component {
    state = { 
        athleteLogin: false, 
        coachLogin: false
     }

     handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state
        if (this.props.user) return <Redirect to="/"/>;
        return (
            <div>
                <button className='choice-button'>Athlete</button> <br/>
                <h1>---OR------</h1>
                <button className='choice-button'>Coach</button>
            </div>
        );
    }
}

export default LoginForm;


