import React, { Component } from 'react'
import { Button, Form, Select } from 'semantic-ui-react'
import API from '../API'



class CreateAccountForm extends Component {
    state = { 
        first_name: undefined, 
        last_name: undefined, 
        email: undefined, 
        account_type: undefined,
        password: undefined, 
        password_confirmation: undefined,
        password_error: false
     }

     handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
     }

     handleAccountTypeChange = e => {
         this.setState({account_type: e.target.querySelector("span").textContent})
     }

     handleSubmit = () => {
        const { first_name, last_name, password, password_confirmation, email } = this.state
        if (password === password_confirmation) {

            if (this.state.account_type === "Coach") {
                this.setState({password_error: false})
                API.createCoachAccount({
                    first_name: first_name,
                    last_name: last_name,
                    password: password,
                    email: email
                })
            } else if (this.state.account_type === "Athlete") {
                API.createAthleteAccount({
                    first_name: first_name,
                    last_name: last_name,
                    password: password,
                    email: email
                })
            }

        } else {
            this.setState({password_error: true})
        }

        // API.loginCoach({
        //     email: this.state.email,
        //     password: this.state.password
        // }).then(user => this.props.onSuccess(user))
    }

    render() {
        const options = [
            { key: 'athlete', value: 'athlete', text: 'Athlete' },
            { key: 'coach', value: 'coach', text: 'Coach' }
          ]
        const { first_name, last_name, password, password_confirmation, email, password_error } = this.state
        return (
            <Form onSubmit={this.handleSubmit}>
                <h1>Create Account</h1>
                <Form.Field>
                    <label>First Name</label>
                    <input type = "text" name = "first_name" value = {first_name} onChange = {this.handleChange}></input>
                </Form.Field>
                <Form.Field>
                    <label>Last Name</label>
                    <input type = "text" name = "last_name" value = {last_name} onChange = {this.handleChange}></input>
                </Form.Field>
                <Form.Field>
                    <label>Email</label>
                    <input type = "text" name = "email" value = {email} onChange = {this.handleChange}></input>
                </Form.Field>
                <Form.Field>
                    {password_error? <h3 style={{color: red}}>Passwords do not match!</h3> : undefined}
                    <label>Password</label>
                    <input type = "password" name = "password" value = {password} onChange = {this.handleChange}></input>
                </Form.Field>
                <Form.Field>
                    <label>Password Confirmation</label>
                    <input type = "password" name = "password_confirmation" value = {password_confirmation} onChange = {this.handleChange}></input>
                </Form.Field>
                <Form.Field>
                    <label>Please choose an account type</label>
                    <Select placeholder='Account Type' onChange = {this.handleAccountTypeChange} options={options} />
                </Form.Field>
                    <Button type='submit'>Create Account</Button>
            </Form>
        );
    }
}

export default CreateAccountForm;
