import React, { Component } from 'react';
import { Grid, Dropdown, Button } from 'semantic-ui-react'
import './WODGenerator.css'

class WODGenerator extends Component {
    state = { 
        type: null, 
        category: null,
        powerlifting: null, 
        done: false
     }


    handleTypeChange = (e, {value}) => {
        this.setState({type: value, done: false})
    }
    handleCategoryChange = (e, {value}) => {
        this.setState({category: value, done: false})
    }
    handlePowerliftingChange = (e, {value}) => {
        this.setState({powerlifting: value, done: true})
    }

    StrengthSetSelector = () => {
        const setTypes = [
           {
               set: [1, 1, 1, 1, 1, 1, 1], 
               percentages: [0.65, 0.75, 0.85, 0.90, 0.95, 0.975, 1.01]
           }, 
           {
            set: [5, 5, 3, 3, 1, 1, 1], 
            percentages: [0.60, 0.70, 0.80, 0.85, 0.90, 0.95, 1.01]
           }

        ]
    }

    // [1, 1, 1, 1, 1, 1, 1],
    // [5, 5, 3, 3, 1, 1, 1], 
    // [5, 5, 5, 5, 5],
    // [10, 3, 3, 3, 10],
    // [10, 5, 5, 5, 10]


    powerliftingSwitch = lift => {
        let percentage = null
        switch (lift) {
            case "deadlift":
                percentage = 0.7;
            break;
            case "back-squat":
                percentage = 0.7;
            break;
            case "front-squat":
                percentage = 0.7;
            break;
            case "bench-press":
                percentage = 0.7;
            break;
        }
    }


    render() {

        const workOptions = [
            {
              key: 'Strength',
              text: 'Strength',
              value: 'strength'
            },
            {
              key: 'Hypertrophy',
              text: 'Hypertrophy',
              value: 'hypertrophy',
            },
            {
              key: 'Metabolic Conditioning',
              text: 'Metabolic Conditioning',
              value: 'metabolic-conditioning'
            },
            {
              key: 'Full Body',
              text: 'Full Body',
              value: 'full-body'
            },
        ]

        const categoryOptions = [
            {
              key: 'olympic Lifting',
              text: 'Olympic-lifting',
              value: 'olympic Lifting'
            },
            {
              key: 'powerlifting',
              text: 'Powerlifting',
              value: 'powerlifting',
            },
            {
              key: 'gymnastics',
              text: 'Gymnastics',
              value: 'gymnastics'
            }
        ]
        const powerliftingOptions = [
            {
              key: 'deadlift',
              text: 'Deadlift',
              value: 'deadlift'
            },
            {
              key: 'back squat',
              text: 'Back Squat',
              value: 'back squat',
            },
            {
              key: 'front-squat',
              text: 'Front Squat',
              value: 'front-squat'
            },
            {
              key: 'bench-press',
              text: 'Bench Press',
              value: 'bench-press'
            }
        ]

        return (
            <> <br/><br/><br/>
            <h1 className='h2'>WOD Generator</h1><br/><br/>
            <Grid columns={2} divided>
                <Grid.Row>
                <Grid.Column>
                    <span className='animated zoomIn dropdown-txt'>
                        I want to work on {' '}
                        <Dropdown
                        inline
                        options={workOptions}
                        defaultValue={""}
                        onChange={this.handleTypeChange}
                        />
                    </span> <br/><br/><br/>
                    {this.state.type === "strength"&&
                    <span className='animated zoomIn dropdown-txt'>
                        Category: {' '}
                        <Dropdown
                        inline
                        options={categoryOptions}
                        defaultValue={""}
                        onChange={this.handleCategoryChange}
                        />
                    </span> 
                    }<br/><br/><br/>
                    {this.state.category === "powerlifting"&&
                    <span className='animated zoomIn dropdown-txt'>
                        Lift: {' '}
                        <Dropdown
                        inline
                        options={powerliftingOptions}
                        defaultValue={""}
                        onChange={this.handlePowerliftingChange}
                        />
                    </span>
                    } <br/><br/><br/>
                    {this.state.done&&
                    <Button>
                        Generate WOD
                    </Button>
                    }
                </Grid.Column>
                <Grid.Column>
                2
                </Grid.Column>
                </Grid.Row>
            </Grid>
            </>
        );
    }
}

export default WODGenerator;
