import React, { Component } from 'react';
import {Link, Redirect} from "react-router-dom"
import { Button, Form, Grid, Menu, Segment } from 'semantic-ui-react'
import './loginForm.css'
import AthleteLogin from '../AthleteComponents.js/AthleteLogin';


class LoginForm extends Component {
    state = { 
        activeItem: "Athlete Login"
     }

     handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {
        const { activeItem } = this.state
        return (
            <div className="container">
                    <Grid>
                        <Grid.Column width={4}>
                        <Menu fluid vertical tabular>
                            <Menu.Item
                            name='Athlete Login'
                            active={activeItem === 'Athlete Login'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='Coach Login'
                            active={activeItem === 'Coach'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='companies'
                            active={activeItem === 'companies'}
                            onClick={this.handleItemClick}
                            />
                            <Menu.Item
                            name='links'
                            active={activeItem === 'links'}
                            onClick={this.handleItemClick}
                            />
                        </Menu>
                        </Grid.Column>

                        <Grid.Column stretched width={12}>
                            <Segment>
                                {activeItem === "Athlete Login" && <AthleteLogin/>} 
                            </Segment>
                        </Grid.Column>
                    </Grid>
            </div>
        );
    }
}

export default LoginForm;


